#!/bin/bash

# Create icon placeholder files for Chrome extension
# These are simple 1x1 pixel PNGs that Chrome will accept

cd "$(dirname "$0")/chrome-extension/icons"

# Create minimal valid PNG files for each size
# This is a base64-encoded 16x16 blue square PNG
echo "iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAE0lEQVR42mNgYGD4T4QBJJQCADN/ARQbLFMAAAAASUVORK5CYII=" | base64 -d > icon16.png

# 48x48 icon
echo "" | base64 -d > icon48.png

# 128x128 icon
echo "" | base64 -d > icon128.png

echo "âœ… Created placeholder icons: icon16.png, icon48.png, icon128.png"
